    SELECT 
        acl.board_id,
        acl.employer_id,
        acl.can_read,
        acl.can_write
    FROM auth.user_tenant_acl acl
    WHERE acl.user_id = v_user_id_bigint
    ORDER BY board_id, employer_id;
END;
$$;


