CREATE FUNCTION auth.apply_std_rls_policy(p_schema text, p_table text, p_read boolean DEFAULT true, p_write boolean DEFAULT true, p_board_col text DEFAULT NULL::text, p_employer_col text DEFAULT NULL::text, p_toli_col text DEFAULT NULL::text) RETURNS void
    LANGUAGE plpgsql SECURITY DEFINER
    AS $$
DECLARE
    read_policy_name text := p_table || '_std_read';
    write_policy_name text := p_table || '_std_write';
    policy_exists boolean;
    read_expr text;
    write_expr text;
    board_expr text;
    employer_expr text;
    toli_expr text;
BEGIN
    -- Enable RLS on the table
    EXECUTE format('ALTER TABLE %I.%I ENABLE ROW LEVEL SECURITY', p_schema, p_table);

    IF p_board_col IS NOT NULL THEN
        board_expr := p_board_col;
    ELSE
        board_expr := 'NULL';
    END IF;
    IF p_employer_col IS NOT NULL THEN
        employer_expr := p_employer_col;
    ELSE
        employer_expr := 'NULL';
    END IF;
    IF p_toli_col IS NOT NULL THEN
        toli_expr := p_toli_col;
    ELSE
        toli_expr := 'NULL';
    END IF;

    read_expr := format('auth.can_read_row((%s)::bigint, (%s)::bigint, (%s)::bigint)', board_expr, employer_expr, toli_expr);
    write_expr := format('auth.can_write_row((%s)::bigint, (%s)::bigint, (%s)::bigint)', board_expr, employer_expr, toli_expr);

    IF p_read THEN
        -- Check if read policy exists
        SELECT EXISTS (
            SELECT 1 FROM pg_policies
            WHERE schemaname = p_schema AND tablename = p_table AND policyname = read_policy_name
        ) INTO policy_exists;

        IF NOT policy_exists THEN
